import React from 'react';
import { 
    SafeAreaView, 
    View, 
    StyleSheet, 
    Text, 
    StatusBar } 
from 'react-native';
import tw from 'twrnc';
import { 
  Inter_400Regular,
  Inter_500Medium,
  Inter_600SemiBold,
  Inter_700Bold,
  Inter_800ExtraBold,
  Inter_900Black 
} from '@expo-google-fonts/inter';
import { useFonts } from 'expo-font';
import AppLoading from 'expo-app-loading';
import axios, { thumbnail_URL } from '../axios_instance';
import { formatDistanceToNowStrict } from 'date-fns';

function ShowPost( { route } ){
    console.log(route.params.id)
    const post_id = route.params.id;

    const [data, setData] = useState('');

    useEffect(() => {
        getPost();
    }, [])

    function getPost(){
        axios.get(`/posts/${post_id}`)
        .then(response => {
            console.log(response);
            setData(response.data);
        })
        .catch(error => {
            console.log(error);
        })
    }

    return(
        <View>
        <Image 
            source={{uri: `${thumbnail_URL}/${item.thumbnail}` }}
            style={tw`w-78.1 h-48 rounded rounded-b-none`}
        />
        <View style={tw`flex-row items-center flex-wrap h-20 justify-between p-2`}>
          <Text
            style={ tw.style('text-xl', {fontFamily: 'Inter_700Bold'})}
          >
              {item.title}
          </Text>
          <Text style={tw`text-xs`}>
              {formatDistanceToNowStrict( new Date(item.created_at))} ago
          </Text>
        </View>
        
        <Text style={ tw.style('p-2 mb-2 text-sm', {fontFamily: 'Inter_500Medium'})}>
            {item.excerpt}
        </Text>
      </View>
    )
}

export default ShowPost;
